import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import Header from "../components/Header";
import Avatar from "../components/Avatar";
import Button from "../components/Button";
import { fire } from "../config/fire";
import Footer from "../components/Footer";

function Profile() {
  let [state, setState] = useState({
    category: "",
    title: "",
    description: ""
  });
  function handleSubmit(e) {
    e.preventDefault();
    if (!state.title || !state.description) {
      alert("please fill in the form");
      return;
    }
    fire
      .firestore()
      .collection("posts")
      .doc(state.category)
      .collection(fire.auth().currentUser.email)
      .doc(state.title)
      .set(state)
      .then(() => {
        setState({
          title: "",
          description: ""
        });
        alert("added successfully!");
      })
      .catch(() => {
        alert("something went wrong");
      });
  }

  function changeHandler(e) {
    let { name, value } = e.target;
    setState({
      ...state,
      [name]: value
    });
  }

  return (
    <div>
      <div>
        <Header />
      </div>
      <Avatar />
      <div className="profile-content">
        <form onSubmit={handleSubmit}>
          <div className="catSelect" onChange={changeHandler}>
            <input type="radio" name="category" value="category 1" />
            category 1
            <input type="radio" name="category" value="category 2" />
            category 2
            <input type="radio" name="category" value="category 3" />
            category 3
            <input type="radio" name="category" value="category 4" />
            category 4
            <input type="radio" name="category" value="category 5" />
            category 5
          </div>
          <div>
            <label>title </label>
            <input value={state.title} onChange={changeHandler} name="title" />
          </div>
          <div>
            <label>description </label>
            <textarea
              value={state.description}
              onChange={changeHandler}
              name="description"
            />
          </div>
          <Button>Add Post</Button>
        </form>
      </div>
      <div>
        <NavLink to="/messages"> Contact Support</NavLink>
      </div>
      <Footer />
    </div>
  );
}

export default Profile;
