import React, { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";
import Header from "../components/Header";
import Avatar from "../components/Avatar";
import Button from "../components/Button";
import { fire } from "../config/fire";
import Footer from "../components/Footer";
import Admin from "./AdminPanel";
import "../styles/profile.css";

function Profile(props) {
  let [state, setState] = useState({
    category: "",
    title: "",
    description: ""
  });

  function handleSubmit(e) {
    e.preventDefault();
    if (!state.category || !state.title || !state.description) {
      alert("please fill in the form");
      return;
    }

    fire
      .firestore()
      .collection("posts")
      .doc()
      .set({ ...state, status: "active", user: fire.auth().currentUser.email })
      .then(() => {
        setState({
          title: "",
          description: ""
        });
      })
      .catch(() => {
        alert("something went wrong");
      });
  }

  function changeHandler(e) {
    let { name, value } = e.target;
    setState({
      ...state,
      [name]: value
    });
  }

  return (
    <div>
      <div>
        <Header />
      </div>
      <div className="profile-content">
        <form onSubmit={handleSubmit}>
          <div className="catSelect" onChange={changeHandler}>
            <label className="radio-container">
              All Categories
              <input type="radio" name="category" value="All Categories" />
              <span className="checkmark"></span>
            </label>

            <label className="radio-container">
              Home
              <input type="radio" name="category" value="Home" />
              <span className="checkmark"></span>
            </label>

            <label className="radio-container">
              Car Services
              <input type="radio" name="category" value="Car Services" />
              <span className="checkmark"></span>
            </label>

            <label className="radio-container">
              Sports & Fitness
              <input type="radio" name="category" value="Sports & Fitness" />
              <span className="checkmark"></span>
            </label>

            <label className="radio-container">
              Beauty
              <input type="radio" name="category" value="Beauty" />
              <span className="checkmark"></span>
            </label>
            <label className="radio-container">
              Other
              <input type="radio" name="category" value="Other" />
              <span className="checkmark"></span>
            </label>
          </div>
          <div>
            {/* <label>title </label> */}
            <input className="title-input" type="text" placeholder="TITLE" value={state.title} onChange={changeHandler} name="title" />
          </div>
          <div>
            {/* <label>description </label> */}
            <textarea
            placeholder="Description"
              value={state.description}
              onChange={changeHandler}
              name="description"
            />
          </div>
          <Button>Add Post</Button>
        </form>
        {props.admin === true ? <Admin /> : null}
      </div>
      <Footer />
    </div>
  );
}

export default Profile;
