import React, { useState, useEffect, useContext } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { fire } from "../config/fire";
import Avatar from "../components/Avatar";
import Post from "../components/Post";
import Search from "../components/Search";
import Add from "../components/Add";
import { UserContext } from "../App";
import Message from "../Message/Message";

function Home(props) {
  let [selection, setSelection] = useState("All Categories");
  let [list, setList] = useState({});
  let user = useContext(UserContext);
  let [s] = useState();

  function handlePostClick(e) {
    e.stopPropagation();
    if (e.target.innerText === "delete") {
      fire
        .firestore()
        .collection("posts")
        .doc(e.target.value)
        .delete();
    }
  }

  useEffect(() => {
    let results = {};

    if (selection === "All Categories") {
      fire
        .firestore()
        .collection("posts")
        .onSnapshot(snapshot => {
          snapshot.forEach(doc => {
            results[doc.id] = doc.data();
          });
          setList(results);
        });
    } else if (selection === "My Posts") {
      fire
        .firestore()
        .collection("posts")
        .where("user", "==", fire.auth().currentUser.email)
        .onSnapshot(snapshot => {
          snapshot.forEach(doc => {
            results[doc.id] = doc.data();
          });
          setList(results);
        });
    } else {
      fire
        .firestore()
        .collection("posts")
        .where("category", "==", selection)
        .onSnapshot(snapshot => {
          snapshot.forEach(doc => {
            results[doc.id] = doc.data();
          });
          setList(results);
        });
    }
  }, [selection, handlePostClick]);

  function handleSelection(e) {
    e.stopPropagation();
    if (e.target.nodeName === "INPUT") {
      setSelection(e.target.value);
    }
  }

  return (
    <div>
      <div onClick={handleSelection}>
        <Header />
      </div>
      <Search />
      <div className="results-container">
        {list &&
          Object.keys(list).map((item, index) => {
            return list[item].status === "deleted" ? null : (
              <div onClick={handlePostClick} key={index}>
                <Post
                  id={item}
                  title={list[item].title}
                  admin={props.admin ? true : false}
                  description={list[item].description}
                  deletable={
                    selection === "My Posts" || user.admin ? true : false
                  }
                />
              </div>
            );
          })}
      </div>
      <Add />

      <Footer />
      {user.admin ? null : fire.auth().currentUser ? <Message /> : null}
    </div>
  );
}

export default Home;
