import React, { useState, useRef, useEffect } from "react";
import { fire } from "../config/fire";
import { stat } from "fs";

import "../styles/post.css";

function Post(props) {
  let postRef = useRef(
    fire
      .firestore()
      .collection("posts")
      .doc(props.id)
  );
  let [status, setStatus] = useState("default"),
    [formData, setFormData] = useState({
      title: props.title,
      description: props.description
    });

  useEffect(() => {}, [setStatus, setFormData, handleClick]);

  function handleChange(e) {
    e.preventDefault();
    let { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  }

  function handleClick(e) {
    e.stopPropagation();
    switch (e.target.innerText) {
      case "cancel":
        setFormData({
          title: props.title,
          description: props.description
        });
        setStatus("default");
        break;
      case "edit":
        setStatus("editing");
        break;
      case "save":
        postRef.current.update({
          title: formData.title,
          description: formData.description
        });
        setStatus("default");
        break;
      case "delete":
        postRef.current.update({
          status: "deleted"
        });
        break;
    }
  }
  return (
    <div className="post">
      {status === "editing" ? (
        <>
          <form>
            <input
              name="title"
              onChange={handleChange}
              value={formData.title}
            />
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
            />
          </form>
        </>
      ) : (
        <>
          <div className="title-window">
            <p className="post-title">{props.title}</p>
          </div>
          <div className="description-window">
            <p className="post-description">{props.description}</p>
          </div>
        </>
      )}

      {props.deletable ? (
        <>
          <button onClick={handleClick} value={props.id}>
            {status === "editing" ? "cancel" : "delete"}
          </button>

          {props.admin ? null : (
            <button onClick={handleClick} value={props.id}>
              {status === "editing" ? "save" : "edit"}
            </button>
          )}
        </>
      ) : null}
    </div>
  );
}
export default Post;
