import React, { useState, useEffect, Suspense, lazy } from "react";
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Redirect
} from "react-router-dom";
import { fire } from "./config/fire";
import LoginComponent from "./login/login";
import SignupComponent from "./signup/signup";
import ErrorBoundary from './ErrorBoundary';


import "./App.css";

const Home = lazy(() => import("./pages/Home"));
const Admin = lazy(() => import("./pages/AdminPanel"));
const Message = lazy(() => import("./Message/Message"));
const Profile = lazy(() => import("./pages/Profile"));

function App() {
  let [signedIn, setSignedIn] = useState(null),
    [admin, setAdmin] = useState(false);

  useEffect(() => {
    fire.auth().onAuthStateChanged(function(user) {
      setSignedIn(!!user);
      if (
        fire.auth().currentUser &&
        fire.auth().currentUser.email === "admin@services-all.com"
      ) {
        setAdmin(true);
      }
    });
  }, []);

  return (
   <ErrorBoundary>
    <UserContext.Provider
      value={{
        signedIn: signedIn,
        admin: admin
      }}
    >    
      <Router>

        <div id="routing-container">
          <Suspense fallback={<h1>Loading...</h1>}>
            <Switch>
              <Route path="/home" component={Home} />

              {signedIn === null ? (
                <h1>Loading...</h1>
              ) : signedIn ? (
                <>
                  {admin ? (
                    <Switch>
                      <Route exact path="/admin-panel" component={Admin} />
                      <Route
                        exact
                        path="/profile"
                        render={props => <Profile {...props} admin={true} />}
                      />
                    </Switch>
                  ) : (
                    <Switch>
                      <Route exact path="/messages" component={Message} />
                      <Route exact path="/profile" component={Profile} />
                    </Switch>
                  )}
                  <Redirect to="/profile" />
                </>
              ) : (
                <>
                  <Route path="/login" component={LoginComponent} />
                  <Route path="/signup" component={SignupComponent} />
                  <Redirect to="/login" />
                </>
              )}
            </Switch>
          </Suspense>
        </div>


       </Router>
    </UserContext.Provider>
</ErrorBoundary>
  );

}

export const UserContext = React.createContext({});
export default App;
